// CMPS390// MaxHeapDriver.java// Kuo-pao Yang/* s = {"D", "F", "I", "C", "H", "A", "E", "J", "B", "G"};           J        /     \       I        F     /    \    /  \    H      G  A    E   / \    /         C   B  D              The Max Heap has 10 items   J I F H G A E C B D              */public class MaxHeapDriver {	public static void main(String[] argv)	{      MaxHeap aHeap = createMaxHeap();      testMaxHeapOperations(aHeap);	}  // end main   	public static MaxHeap createMaxHeap() {		MaxHeap aHeap = new MaxHeap();	      String[] s = {"D", "F", "I", "C", "H", "A", "E", "J", "B", "G"};		System.out.println("Testing add()");      for (int i=0; i < s.length; i++) {         System.out.print(s[i] + " ");         aHeap.add(s[i]);      }      aHeap.display();      return aHeap;   } // creatMaxHeap	   public static void testMaxHeapOperations(MaxHeap aHeap)	{	   System.out.println("Testing isEmpty() returns = " + aHeap.isEmpty() + " (should be false)");	   System.out.println("Testing getMax() returns = " + aHeap.getMax() + " (should be J)"); 				System.out.println("Removing entries in descending order:");		while (!aHeap.isEmpty())			System.out.print(aHeap.removeMax() + " ");      System.out.println("(should be J I H G F E D C B A)");   } // end testMaxHepOperations}  // end MaxHeapDriver/* OUTPUT   Testing add()   D F I C H A E J B G    The Max Heap has 10 items   J I F H G A E C B D    Testing isEmpty() returns = false (should be false)   Testing getMax() returns = J (should be J)   Removing entries in descending order:   J I H G F E D C B A (should be J I H G F E D C B A)*/